as\n","lista_de_compras <- list(\n","  frutas = c(\"Maçã\", \"Banana\", \"Laranja\"),\n","  vegetais = c(\"Alface\", \"Tomate\", \"Cenoura\"),\n","  laticinios = c(\"Leite\", \"Queijo\"),\n","  outros = c(\"Pão\", \"Arroz\", \"Feijão\")\n",")\n","\n","# Preços dos itens\n","precos <- c(\n","  Maçã = 2.5, Banana = 1.8, Laranja = 2,\n","  Alface = 1.2, Tomate = 2.3, Cenoura = 1.5,\n","  Leite = 3, Queijo = 5,\n","  Pão = 2, Arroz = 4, Feijão = 3.5\n",")\n","\n","# Calcular o total da compra\n","total <- 0\n","for (categoria in lista_de_compras) {\n","  for (item in categoria) {\n","    total <- total + precos[item]\n","  }\n","}\n","\n","# Imprimir a lista de compras e o total\n","cat(\"Lista de compras:\\n\")\n","for (categoria in names(lista_de_compras)) {\n","  cat(categoria, \":\", paste(lista_de_compras[[categoria]], collapse = \", \"), \"\\n\")\n","}\n","cat(\"\\nTotal da compra: $\", total, \"\\n\")\n"],"metadata":{"colab":{"base_uri":"https://localhost:8080/"},"id":"Wj2MfEe3f3Xr","executionInfo":{"status":"ok","timestamp":1711049509268,"user_tz":180,"elapsed":319,"user":{"displayName":"AILTON JOSE DOS SANTOS","userId":"16817185551407619640"}},"outputId":"c8b0d1d8-99a3-4743-8190-bf83133b108f"},"execution_count":2,"outputs":[{"output_type":"stream","name":"stdout","text":["Lista de compras:\n","frutas : Maçã, Banana, Laranja \n","vegetais : Alface, Tomate, Cenoura \n","laticinios : Leite, Queijo \n","outros : Pão, Arroz, Feijão \n","\n","Total da compra: $ 28.8 \n"]}]}]}